mapper.select:
select
trainer_no
member_no
from member_mapping where training_cnt != training_maxcnt
and member_no = #{memberNo}
and trainer_no = #{memberNo}

mapper.update:
update member set
	zip = #{zip},
	road_Addr = #{roadAddr},
	det_Addr = #{detAddr}
where member_no = #{memberNo}

update member set
	expire_date = sysdate+30
where member_no = #{memberNo}

update member set
	expire_date = sysdate+1
where member_no = #{memberNo}

update member set
	expire_date = sysdate+365
where member_no = #{memberNo}
